/** Global definitions for developement **/

// for style loader

declare module '*.scss' {
  const styles: any;
  export = styles;
}
declare module '*.css' {
  const styles: any;
  export = styles;
}

declare module '*.svg' {
  const data: string;
  export = data;
}

declare module '*.png' {
  const data: string;
  export = data;
}

declare module '*.gif' {
  const data: string;
  export = data;
}